import React from 'react';
import { useLocalState } from '../util/useLocalStorage';
import { useEffect, useState } from 'react';
import { LogOut } from '../services/LogOut';
import Settings from '../Settings';
import { jwtDecode } from "jwt-decode";
import {
  Button, Form, Row, Col, Container, Navbar,
  Nav, InputGroup, ButtonGroup, FormLabel, ToggleButton, ToggleButtonGroup,
  NavbarText, NavDropdown, Table, Pagination, Card, CardFooter, CardBody
} from 'react-bootstrap';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import DateTimePicker from 'react-datetime-picker';
import Clock from 'react-clock';
import 'react-clock/dist/Clock.css';
import 'react-datetime-picker/dist/DateTimePicker.css';
import TimePicker from 'react-time-picker';
import 'react-time-picker/dist/TimePicker.css';
import { useNavigate } from 'react-router-dom';

const User = () => {
  /* const [jwt, setJwt] = useLocalState("", "jwt");
   const [roles, setRoles] = useLocalState(UseGetRoleFromJWT);
   if (jwt) {
     try {
       const decoded = jwtDecode(jwt);
 
       console.log(decoded.authorities);
       console.log("Ovo vracas:" + decoded.roles[0].authority);
       if (decoded.roles[0].authority === "ADMIN") {
         window.location.href = "/admin";
         return <div>Loading....</div>
 
       } else if (decoded.roles[0].authority !== "USER") {
         window.location.href = "/welcome";
         return <div>Loading...</div>
 
       }
     }
     catch (error) {
       window.location.href = "/welcome";
       return <div>Loading....</div>
     }
   }
 
   function UseGetRoleFromJWT() {
     if (jwt) {
       const decoded = jwtDecode(jwt);
       console.log(decoded.authorities);
 
       return decoded.roles[0].authority;
     }
     else {
       return "";
     }
   }
 
   console.log("idem dalje");
 
 */



  //VARIJABLE
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [currentPage, setCurrentPage] = useState(1);
  const [currentPage1, setCurrentPage1] = useState(1);
  const itemsPerPage = 10;
  const [data, setData] = useState([]);
  const [description, setDescription] = useState('');
  const navig = useNavigate();

  //HANDLE funckije

  const handleReject = () => {
    //izbrisi termin (auth/reject?)
    // /api/v1/auth/appointment/reject/{appointmentId}
  };


  const handleReload = () => {
    window.location.reload()
  }

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };



  const handlePageChange1 = (pageNumber) => {
    setCurrentPage1(pageNumber);
  };

  // formatiranje za datum, moguce nepotrebno

  const newDate = new Date(selectedDate);
  newDate.setHours(selectedDate.getHours() + 1);

  const formattedDateTime = newDate.toISOString().slice(0, 19).replace("T", " ");







  // Salji zahtjeve



  const handleSubmission = () => {


    if (!description.trim()) {
      alert('Molimo unesite opis bolesti.');
      return;
    }

    const selectedDay = selectedDate.getDay();
    if (selectedDay === 0 || selectedDay === 6) {
      alert('Izabrani dan je vikend, molimo izaberite radni dan.');
      return;
    }

    const minutes = selectedDate.getMinutes();

    if (minutes !== 0) {
      alert('Molimo izaberite samo pune sate.');
      return;
    }

    const time = selectedDate.getHours();
    if (time < 8 || time > 19) {
      alert('Molimo izaberite vrijeme izmeÄ‘u 8:00 i 19:00 sati.');
      return;
    }


    const jsonData = {
      description: description,
      date: formattedDateTime,
    };

    setDescription('');
    setSelectedDate(new Date());
    try {


      fetch("http://localhost:8080/api/v1/auth/appointment/request", {
        headers: {
          "Content-Type": "application/json",
        },
        method: "POST",
        body: jsonData,
      })
        .catch((error) => {
          console.error("Error:", error)
        });
    }

    catch (error) {
      return Promise.reject("Invalidni zahtjev");
      ;
    }
    console.log(jsonData);

  };


  //funkcije



  useEffect(() => {
    const fetchData = async () => {
      try {

        const response = await fetch('http://localhost:8080/api/v1/auth/appointment/request/all-appointments');
        const jsonData = await response.json();
        setData(jsonData);
      } catch (error) {
        console.error('Empty', error);
      }
    };
    fetchData();
  }, []);


  //Pagination and calendar setup

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = data.slice(indexOfFirstItem, indexOfLastItem);

  const today = new Date();
  const yearPlaceholder = today.getFullYear().toString();
  const monthPlaceholder = (today.getMonth() + 1).toString();
  const dayPlaceholder = today.getDate().toString();

  const currentDay = today.getDay();

  let maxDate;
  if (currentDay >= 1 && currentDay <= 4) {

    maxDate = new Date(today.getTime() + (11 - currentDay) * 24 * 60 * 60 * 1000 + 24 * 60 * 60 * 1000);
  }
  else {

    maxDate = new Date(today.getTime() + 14 * 24 * 60 * 60 * 1000 + 24 * 60 * 60 * 1000);
  }



  const data1 = Array.from({ length: 74 }, (_, index) => {
    const today = new Date();
    const currentDate = new Date(today.setDate(today.getDate() + index));
    const day = currentDate.getDate();
    const month = currentDate.getMonth() + 1;
    const year = currentDate.getFullYear();
    return {
      id: index + 1,
      appointment: `Termin ${index + 1}`,
      description: `Description ${index + 1}`,
      equipment: `Equipment ${index + 1}`,
      room: `Room ${index + 1}`,
      date: `${day}.${month}.${year}.`,
      time: `11:00`
    };
  });

  const handleButtonClick = () => {

  }
  const indexOfLastItem1 = currentPage1 * itemsPerPage;
  const indexOfFirstItem1 = indexOfLastItem1 - itemsPerPage;
  const currentItems1 = data1.slice(indexOfFirstItem1, indexOfLastItem1);




  return (
    <Container className="justify-content-md-center">
      <>
        <style type="text/css">
          {`
             .purple {
               background-color: purple;
               color: white;
             }
             .purple .navbar-brand {
               color: white;
             }
 
             .purple .navbar-brand img {
               width: 150px;
               height: 75px;
             }
 
             .bigBoldText {
               font-size: 20px;
               font-weight: bold;
             }

             .descriptionName{
              font-size: 17px;
              font-weight: 500;
             }
 
             .pageName {
               font-size: 27px;
               font-weight: bold;
             }
 
             .tablica th, .tablica td {
               text-align: center;
               vertical-align: middle;
             }

             .card{
              vertical-align: middle;
             }

             .dateTime{
              width: 900px;
             }
           `}
        </style>
      </>
      <Navbar //NASLOV / HEADER
        className='purple' fixed="top">
        <Container>
          <Col xs={1}>
            <Navbar.Brand href="#">
              <img
                src="/logofr.jpeg"
                width="150"
                height="75"
                className="d-inline-block align-items-start rounded"
                alt="logo"
              />
            </Navbar.Brand>
          </Col>
          <Col xs={8}>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav" className="justify-content-center">
              <Navbar.Brand className='pageName'>Pacijent</Navbar.Brand>
            </Navbar.Collapse>
          </Col>
          <Col xs={1}>
            <Navbar.Collapse className="justify-content-end">
              <Nav>
                <NavDropdown title="Opcije" id="basic-nav-dropdown" className="bigBoldText">
                  <NavDropdown.Item onClick={() => window.location.href = "/settings"}>Postavke</NavDropdown.Item>
                  <NavDropdown.Divider />
                  <NavDropdown.Item onClick={() => LogOut()}>Odjavi se</NavDropdown.Item>
                </NavDropdown>
              </Nav>
            </Navbar.Collapse>
          </Col>
        </Container>
      </Navbar>

      <br /><br /><br /><br /><br /><br /><br />


      <Container //TABLICA ZAKAZANIH TERMINA
      >
        <FormLabel className='descriptionName'>
          VaÅ¡i termini:
        </FormLabel>
        <Table className='tablica' striped bordered hover>
          <thead>
            <tr>
              <th>"Korisnicko ime"</th>
              <th>Opis</th>
              <th>Oprema</th>
              <th>Prostorija</th>
              <th>Datum</th>
              <th>Vrijeme</th>
              <th>OtkaÅ¾i</th>
            </tr>
          </thead>
          <tbody>
            {//DODAJ PRAVI DATA
              currentItems.length === 0 ? (
                <tr>
                  <td colSpan={7} className="text-center">
                    Nemate zakazanih termina
                  </td>
                </tr>
              ) : (

                currentItems.map((item) => (
                  <tr key={item.id}>
                    <td>{item.appointment}</td>
                    <td>{item.description}</td>
                    <td>{item.equipment}</td>
                    <td>{item.room}</td>
                    <td>{item.date}</td>
                    <td>{item.time}</td>
                    <td>
                      <Button
                        variant="link"
                        onClick={() => handleReject()}

                      >
                        OtkaÅ¾i ovaj termin
                      </Button>
                    </td>
                  </tr>
                )))}
          </tbody>
        </Table>

        <br />

        <Container //Pagination
          className='d-flex justify-content-center'>
          <Pagination>
            {Array.from({ length: Math.ceil(data.length / itemsPerPage) }, (_, index) => index + 1).map((pageNumber) => (
              <Pagination.Item
                key={pageNumber}
                active={pageNumber === currentPage}
                onClick={() => handlePageChange(pageNumber)}
              >
                {pageNumber}
              </Pagination.Item>
            ))}
          </Pagination>
        </Container>
        <br />
        <Container className='d-flex justify-content-center'>
          <Button
            variant='primary'
            onClick={() => handleReload()}
          >
            OsvjeÅ¾i
          </Button>
        </Container>


      </Container>

      <br /><br /><br />


      <Container //KARTICA
        className='d-flex justify-content-center'>
        <Card border="primary" style={{ width: '47rem' }}>
          <Card.Header as='h4' >
            <Container className='d-flex justify-content-center'>
              Molimo ispunite zahtjev za novi termin:
            </Container>
          </Card.Header>
          <CardBody>
            <Row >
              <Container //OPISI BOLEST TEXT
              >
                <Form>
                  <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
                    <Form.Label className='descriptionName'>Unesite opis zdravstvenog problema:</Form.Label>
                    <Form.Control
                      as="textarea"
                      rows={2}
                      value={description}
                      onChange={handleDescriptionChange}
                    />
                  </Form.Group>
                </Form>
              </Container>
            </Row>
          </CardBody>

          <CardBody>
            <Row>
              <Form.Label className='descriptionName'>
                Unesite datum i vrijeme Å¾eljenog termina:
              </Form.Label>
            </Row>
            <Row>
              <FormLabel>
                Radno vrijeme klinike:
                Pon - Pet, od 8:00 do 20:00 sati
              </FormLabel>
            </Row>
            <Row>
              <FormLabel>
                Termini traju 60 minuta.
              </FormLabel>
            </Row>

          </CardBody>



          <CardBody>
            <Container className='d-flex justify-content-center'>
              <DateTimePicker
                minDate={new Date(
                  //new Date().getTime() + 24 * 60 * 60 * 1000
                )}
                maxDate={maxDate}
                disableWeekends={true}
                disableClock={true}
                disableCalendar={true}
                clearIcon={null}
                yearPlaceholder={yearPlaceholder}
                monthPlaceholder={monthPlaceholder}
                dayPlaceholder={dayPlaceholder}
                minTime={new Date(2000, 0, 1, 8, 0)}
                maxTime={new Date(2000, 0, 1, 20, 0)}
                //onInvalidChange={() => alert('Invalid datetime')}
                onChange={(date) => setSelectedDate(date)}
                value={selectedDate}
              />
            </Container>
          </CardBody>



          <Card.Body >
            <Row>
              <Col xs={6} className='d-flex justify-content-center align-items-center'>
                <Calendar
                  onChange={setSelectedDate}
                  value={selectedDate}
                />
              </Col>
              <Col xs={6} className='d-flex justify-content-center align-items-center'>
                <Clock
                  renderNumbers={true}
                  renderSecondHand={false}
                  value={selectedDate}
                />
              </Col>
            </Row>
            <br />
          </Card.Body>


          <CardFooter>
            <Container className='d-flex justify-content-center'>

              <Button
                variant='primary' type='submit'
                onClick={handleSubmission}
              > Potvrdi odabir </Button>
            </Container>
          </CardFooter>
        </Card>
      </Container >


      <br /> <br /><br />



      <Container //TABLICA ZA NOVE TERMINE
      >
        <FormLabel className='descriptionName'>
          Odaberite novi termin:
        </FormLabel>
        <Table className='tablica' striped bordered hover>
          <thead>
            <tr>
              <th>"Korisnicko ime"</th>
              <th>Opis</th>
              <th>Oprema</th>
              <th>Prostorija</th>
              <th>Datum</th>
              <th>Vrijeme</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {currentItems1.map((item) => (
              <tr key={item.id}>
                <td>{item.appointment}</td>
                <td>{item.description}</td>
                <td>{item.equipment}</td>
                <td>{item.room}</td>
                <td>{item.date}</td>
                <td>{item.time}</td>
                <td> <Button variant="link"
                  onClick={() => handleButtonClick(item.date)}
                > Odaberi ovaj termin </Button> </td>
              </tr>
            ))}
          </tbody>
        </Table>

        <br />

        <Container className='d-flex justify-content-center'>
          <Pagination>
            {Array.from({ length: Math.ceil(data1.length / itemsPerPage) }, (_, index) => index + 1).map((pageNumber) => (
              <Pagination.Item
                key={pageNumber}
                active={pageNumber === currentPage1}
                onClick={() => handlePageChange1(pageNumber)}
              >
                {pageNumber}
