import React from "react";
import { useLocalState } from "../../util/useLocalStorage";
import { jwtDecode } from "jwt-decode";

import { useEffect } from "react";
import { useState } from "react";
import {Navigation} from "../../services/navigate";
import {
  Button,
  Form,
  Row,
  Col,
  Container,
  Navbar,
  Nav,
  InputGroup,
  ButtonGroup,
  FormLabel,
  ToggleButton,
  ToggleButtonGroup,
  NavbarText,
} from "react-bootstrap";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [roles, setRoles] = useState('');

  const [jwt, setJwt] = useLocalState("", "jwt");
  const [loginSource, setLoginSource] = useState("", "loginSource");



  

  


  //NE Diraj Fetch ili ista ispod

  console.log(username);
  console.log(password);
  let pom = false;

  function sendLoginRequest() {
    pom = false;

    console.log("kliknut");
    const reqBody = {
      username: username,
      password: password,
    };

   

    fetch("http://localhost:8080/api/v1/auth/authenticate", {
      headers: {
        "Content-Type": "application/json",
      },
      method: "POST",

      body: JSON.stringify(reqBody),
    })
      .then((response) => {
        if (response.status === 200) {
          pom = true;

          setLoginSource(true);
          return response.json();
        } else {
          console.log("nije usao");
          return Promise.reject("Invalid username or password");
        }
      })
      .then((data) => {
        if (pom === true) {
          console.log("usao", data.token);
          setJwt(data.token);
          window.location.href = "/user";
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }

  //





  useEffect(() => {
    function updateRoles() {
      if (jwt) {
        console.log(jwt);
        const decoded = jwtDecode(jwt);
        const roles = decoded.roles;

        if (roles && roles.length > 0) {
          const role = roles[0];
          console.log("Role:" + role);
          localStorage.setItem("roles", role);
          console.log("Success");
        } else {
          console.log("No roles found in token");
        }
      } else {
        localStorage.removeItem('roles'); // remove roles from localStorage
      }
    }

    updateRoles();
  }, [jwt]);


 

  return (
    <Container className="justify-content-md-center">
      <Navbar bg="dark" data-bs-theme="dark" fixed="top">
        <Container className="justify-content-space-between">
          <Col xs={1}>
            <Navbar.Brand href="#">
              <img
                src="/logofr.jpeg"
                width="150"
                height="75"
                className="d-inline-block align-items-start rounded"
                alt="logo"
              />
            </Navbar.Brand>
          </Col>
          <Col xs={8}>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse
              id="basic-navbar-nav"
              className="justify-content-center"
            >
              <Navbar.Brand>Prijava</Navbar.Brand>
            </Navbar.Collapse>
          </Col>

          <Col xs={1}>
            <Navbar.Collapse className="justify-content-end">
              <Nav>
                <Button
                  variant="outline-primary"
                  onClick={() => (window.location.href = "/welcome")}
                >
                  Povratak
                </Button>
              </Nav>
            </Navbar.Collapse>
          </Col>
        </Container>
      </Navbar>

      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />

      <Form noValidate>
        <Row className="justify-content-md-center">
          <Col xs={6}>
            <Form.Group md="3" controlId="validationCustom01">
              <Form.Label>Korisničko ime</Form.Label>
              <Form.Control
                placeholder="Korisničko ime"
                type="username"
                id="username"
                value={username}
                onChange={(event) => setUsername(event.target.value)}
                isValid={!username.includes(" ") && username.length > 0}
                isInvalid={username.includes(" ") && username.length > 0}
                required
              />
              <Form.Control.Feedback type="invalid">
                Molimo unesite ispravno korisničko ime.
              </Form.Control.Feedback>
            </Form.Group>
          </Col>
        </Row>
        <Row className="justify-content-md-center">
          <Col xs={6}>
            <Form.Group md="3" controlId="validationCustom02">
              <Form.Label>Lozinka</Form.Label>
              <Form.Control
                type="password"
                placeholder="Lozinka"
                required
                id="password"
                value={password}
                onChange={(event) => setPassword(event.target.value)}
                isValid={
                  password.length > 7 &&
                  password.trim().length < 21 &&
                  !password.includes(" ")
                }
                isInvalid={
                  password.length > 0 &&
                  !(
                    password.length > 7 &&
                    password.trim().length < 21 &&
                    !password.includes(" ")
                  )
                }
              />

              <Form.Control.Feedback type="invalid">
                Molimo unesite ispravnu lozinku.
              </Form.Control.Feedback>
            </Form.Group>
          </Col>
        </Row>

        <br />
        <br />
        <br />
        <Row className="justify-content-md-center">
          <Col xs={2}>
            <Button className="mb-3" onClick={() => sendLoginRequest()}>
              Prijavi se
            </Button>
          </Col>
        </Row>
      </Form>
    </Container>

    /*
      <>
      <div>
          <label>Username</label>
          <input type="username" id="username" value={username} onChange={(event) => setUsername(event.target.value)}/>
      </div>
      <div>
          <label>Password</label>
          <input type="password" id="password" value={password} onChange={(event)=> setPassword(event.target.value)}/>
      </div>
      <div>
          <button id="submit" type="button" onClick={() => sendLoginRequest()}>Login</button>
      </div>


     

    </>*/
  );
};

export default Login;
