import React, { useState } from 'react';
import { useLocalState } from '../util/useLocalStorage';
import { useEffect } from 'react';
import { jwtDecode } from "jwt-decode";
import { LogOut } from '../services/LogOut';
import { Button, Form, Row, Col, Container, Navbar, Nav } from 'react-bootstrap';

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [jwt, setJwt] = useLocalState("", "jwt");
  const [loginError, setLoginError] = useState(""); // Dodano stanje za poruku o pogrešci

  useEffect(() => {
    if (jwt) {
      const decoded = jwtDecode(jwt);
      if (decoded.roles[0].authority === "ADMIN") {
        window.location.href = "/admin";
      }
      if (decoded.roles[0].authority === "USER") {
        window.location.href = "/user";
      }
    }
  }, [jwt]);

  const sendLoginRequest = async () => {
    try {
      const reqBody = {
        "username": username,
        "password": password,
      };

      const response = await fetch("http://localhost:8080/api/v1/auth/authenticate", {
        headers: {
          "Content-Type": "application/json",
        },
        method: "POST",
        body: JSON.stringify(reqBody),
      });

      if (response.ok) {
        const data = await response.json();
        setJwt(data.token);
        window.location.href = "/user";
      } else {
        setLoginError("Invalid username or password"); // Postavljanje poruke o pogrešci
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <Container className="justify-content-md-center">
      {/* ... Ostatak vašeg koda ... */}
      <Row className="justify-content-md-center">
        <Col xs={6}>
          {loginError && <div style={{ color: 'red' }}>{loginError}</div>} {/* Prikaz poruke o pogrešci */}
        </Col>
      </Row>
      <Row className="justify-content-md-center">
        <Col xs={2}>
          <Button
            className="mb-3"
            onClick={() => sendLoginRequest()}
          >Prijavi se</Button>
        </Col>
      </Row>
    </Container>
  );
};

export default Login;
