const TimerComponent = () => {
    const [countdown, setCountdown] = useState(60); // Initial countdown in seconds
    const [isTokenValid, setIsTokenValid] = useState(true); // Initial state for token validity
  
    // Function to decrement the countdown
    const decrementCountdown = () => {
      setCountdown((prevCountdown) => prevCountdown - 1);
    };
  
    // Function to check JWT validity
    const CheckJwt = () => {
      // Perform your JWT validity check logic here
      // For demonstration purposes, assume a function isTokenValid(token) is used
      // Replace it with your actual logic to check the JWT validity
      const token = localStorage.getItem('jwtToken');
      setIsTokenValid(isTokenValid(token));
    };
  
    useEffect(() => {
      // Check JWT validity initially
      CheckJwt();
  
      // Schedule the decrementCountdown function to run every second
      const timerId = setInterval(() => {
        decrementCountdown();
  
        // Check JWT validity every 60 seconds
        if (countdown % 60 === 0) {
          CheckJwt();
        }
      }, 1000);
  
      // Cleanup function to clear the interval when the component is unmounted
      return () => clearInterval(timerId);
    }, [countdown]);
  
    return (
      <div>
        <p>Countdown: {countdown} seconds</p>
      </div>
    );
  };






