    return (
        <div>

            <h1>Usao si</h1>
            <div>jtw is {jwt}</div>
            <div>
            <button id="logout" type="button" onClick={() => LogOut()}>LogOut</button>
            </div>

            <div>
            <button id="settings" type="button" onClick={() => window.location.href = "/settings"}>Settings</button>
            </div>

        </div>
    );
};

export default User;
