import User from './User';
import Welcome from './Welcome';
import Login from './Login';
import PrivateRoutes from './PrivateRoutes';





function App() {


  const [loginSource, setLoginSource] = useLocalState("");
  const [jwt,setJwt] = useLocalState("");


 // store current content in a variable
var currentContent = $('body').html();// compare local storage with current content
if (localStorage.getItem('monitorContent') != currentContent) {
    // play sound here...
}// set local storage to current content
localStorage.setItem('monitorContent', currentContent);



